import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const AddressList = ({ addresses, setAddresses }) => {
  const [confirmDelete, setConfirmDelete] = useState(null);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/addresses/${id}`);
      setAddresses(addresses.filter((address) => address.id !== id));
      setConfirmDelete(null); // Limpa o estado de confirmação após a exclusão
    } catch (error) {
      console.error('Error deleting address:', error);
    }
  };

  return (
    <div>
      <h2>Lista de Endereços</h2>
      {addresses && addresses.length > 0 ? (
        <ul>
          {addresses.map((address) => (
            <li key={address.id}>
              <strong>{address.lot}</strong>
              <p>{address.otherDetails}</p>
              <div>
                <Link to={`/edit/${address.id}`}>Editar</Link>
                <button onClick={() => setConfirmDelete(address.id)}>Excluir</button>
              </div>
              {confirmDelete === address.id && (
                <div>
                  <p>Deseja realmente excluir este endereço?</p>
                  <button onClick={() => handleDelete(address.id)}>Sim</button>
                  <button onClick={() => setConfirmDelete(null)}>Cancelar</button>
                </div>
              )}
            </li>
          ))}
        </ul>
      ) : (
        <p>Nenhum endereço encontrado.</p>
      )}
      <Link to="/create">Cadastrar Novo Endereço</Link>
    </div>
  );
};

export default AddressList;
