import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import AddressList from '../components/AddressList';
import AddressForm from '../components/AddressForm';

const CreateAddress = () => {
  const [addresses, setAddresses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAddresses = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/addresses');
        setAddresses(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching addresses:', error);
      }
    };

    fetchAddresses();
  }, []);

  const saveAddress = async (address) => {
    try {
      await axios.post('http://localhost:5000/api/addresses', address);
      setAddresses([...addresses, address]); // Adiciona o novo endereço à lista
    } catch (error) {
      console.error('Error saving address:', error);
    }
  };

  return (
    <div>
      <h1>Cadastrar Novo Endereço</h1>
      <AddressForm onSave={saveAddress} />
      <hr />
      <h2>Endereços Cadastrados</h2>
      {loading ? (
        <p>Carregando endereços...</p>
      ) : (
        <AddressList addresses={addresses} setAddresses={setAddresses} />
      )}
      <Link to="/">Voltar para a Lista de Endereços</Link>
    </div>
  );
};

export default CreateAddress;
